import React, { useState,useEffect } from "react";
import { Link } from "react-router-dom";
import {RiFileDownloadFill} from 'react-icons/ri';
import {FcDownload} from 'react-icons/fc';
import Individual from './individuasl';
import jsPDF from 'jspdf';
import * as autoTable from 'jspdf-autotable';
import * as FileSaver from 'file-saver';
import * as XLSX from 'xlsx';

export default function ResultsList({ list, updateId, updateIndex, Index }) {
  const [sorting,setsort]=useState(0)
  let vals=list.slice(Index.start, Index.end)
  const [data,setData]=useState(vals[0])
  const [individualIndex,setIndividualindex]=useState(0)

useEffect(()=>{
  setData(vals[0])
  setIndividualindex(0)
},[list,Index.start,sorting])  
  
  if(!sorting){
    list.sort((a, b) => a.faculty_id.localeCompare(b.faculty_id))

  }
  if(sorting){
    list.sort((a, b) => b.faculty_id.localeCompare(a.faculty_id))

  }


  const changeIndexHandler = (e) => {
    setIndividualindex(0)
    
    let operation = e.target.getAttribute("data-operation");
    if (operation === "-")
      if (Index.start > 0)
        updateIndex({
          start: Index.start - 18,
          end: Index.end - 18,
        });
      else if (Index.end <= list.length)
        updateIndex({
          start: 0,
          end: 18,
        });
      else
        updateIndex({
          start: Index.start,
          end: Index.end,
        });
    else if (Index.end <= list.length)
      updateIndex({
        start: Index.start + 18,
        end: Index.end + 18,
      });
      
  };

  const generatePdf=()=>{
    var doc = new jsPDF('landscape','px','a4');
    
    doc.autoTable({
      styles: {halign:'center',valign:"middle",font:'helevetica' },
      theme:'grid',
      headStyle:{fillColor:(165,42,42)
      },
      
      head: [['FACULTY_ID','FACULTY_NAME','DEPARTMENT','DESIGNATION','FAP_2021','FRS_2021','FRP_2021','FEEDBACK_2021']],
        
        body:list.map(list=>[[list.faculty_id],[list.name],[list.department],[list.designation],[list.FAP_2021_Score],[list.FRS_2021],[list.FRP_2021],[list.Feedback_2021_Score]]),
       
        
      })
    

    doc.save('table.pdf'); 

  };
  const out=list.map(list=>{
   delete list.id;
   delete list.central_responsibility;
   delete list.About;
   delete list.status;
   return list
  })
  // console.log("list",out)

  const fileType = 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet;charset=UTF-8';
    const fileExtension = '.xlsx';
    const fileName="download"

    const exportToCSV = (list, fileName) => {
        const ws = XLSX.utils.json_to_sheet(out);
        const wb = { Sheets: { 'data': ws }, SheetNames: ['data'] };
        const excelBuffer = XLSX.write(wb, { bookType: 'xlsx', type: 'array' });
        const data = new Blob([excelBuffer], {type: fileType});
        FileSaver.saveAs(data, fileName + fileExtension);
    }

  
  return (
    <div className="display">
    <div className="results-list-container">
      <div className="results-list-page-nav">
        <p>Results </p>
        <button data-operation="-" onClick={changeIndexHandler}>
          &lt;
        </button>{" "}
        {Index.start} - {Index.end}
        <button data-operation="+" onClick={changeIndexHandler}>
          &gt;
        </button>{" "}
      </div>
      <h3 style={{backgroundColor:'rgb(25, 140, 255)',
    color:'white',
    textAlign:'center',
    padding:'10px'}}onClick={()=>setsort(!sorting)}>FACULTY LIST</h3>

      {list.length > 0 ? (
        <div className="results">
        
        <ul>
          {vals.map((x,index) => (
          
            <li onClick={()=>{setData(x)
              setIndividualindex(index)
            }}>
              <p>
              <img src="https://upload.wikimedia.org/wikipedia/en/7/77/Bannari_Amman_Institute_of_Technology_logo.png" style={{height:'30px',width:'50px',padding:'0px'}}/>
              <span style={{textAlign:'center',margin:'10px',height:'44px'}}> {`${x.faculty_id} - ${x.name}`}</span></p>
            </li>
           
          ))}
        </ul>
        </div>
      ) : (
        <span>No Data Found</span>
        
      )}
      <button onClick={generatePdf}style={{padding:'7px 10px',margin:'7px',textAlign:"center",backgroundColor:'lightgreen',fontSize:'17px',width:"140px",height:"45px",borderRadius:"20px"}}>      <RiFileDownloadFill size="20px"/>
PDF FILE</button><br/>
      <button className="button" onClick={(e)=>exportToCSV(list,fileName)}style={{padding:'7px 10px',margin:'7px',textAlign:"center",backgroundColor:'lightblue',fontSize:'17px',height:"45px",width:"140px",borderRadius:"20px"}}><FcDownload size="25px"/>
EXCEL</button>

    </div><br/>

    {data==undefined?'':<Individual data={data} vals={vals} setData={setData} individualIndex={individualIndex} setIndividualindex={setIndividualindex}/>}

    </div>
  );
}
