import React, { useEffect, useState } from "react";
import axios from "axios";
import {RiFileDownloadFill} from 'react-icons/ri';
import {FcDownload} from 'react-icons/fc';
import jsPDF from 'jspdf';
import * as autoTable from 'jspdf-autotable';
import * as FileSaver from 'file-saver';
import * as XLSX from 'xlsx';

export default function Individual({ data,list  ,vals,setData,individualIndex,setIndividualindex}) {
  const [fieldGroup, setFieldGroup ] = useState([])

  

  useEffect(()=>{
    setIndividualindex(0)
    setFieldGroup(Object.keys(data).filter((a)=>a.includes('Feedback') || a.includes('FRP') || a.includes('FAP') || a.includes('FRS')))
  }, [])
  const year=['2019-2020','2020-2021','2021-2022']
console.log(individualIndex)
  const changeHandler=(e)=>{
    let operation = e.target.getAttribute("data-operation");
    if (operation === "+"){

      if(individualIndex>=17 || individualIndex>=vals.length-1){
        setIndividualindex(0)
        setData(vals[0])
      }
else{
  setIndividualindex(individualIndex+1)


  setData(vals[individualIndex+1])

}    
}
if (operation === "-"){
  if(individualIndex<=0){
    setIndividualindex(vals.length-1)
    setData(vals[vals.length-1])
  }
else{
setIndividualindex(individualIndex-1)


setData(vals[individualIndex-1])

}    
   
}
  }

  const generatePdf=()=>{
    var doc = new jsPDF('landscape','px','a4');
    
    doc.autoTable({
      styles: {halign:'center',valign:"middle",font:'helevetica' },
      theme:'grid',
      headStyle:{fillColor:(165,42,42)
      },
      
      head: [['FACULTY_ID','FACULTY_NAME','DEPARTMENT','DESIGNATION','FAP_2021','FRS_2021','FRP_2021','FEEDBACK_2021']],
        
        body:list.map(list=>[[list.faculty_id],[list.name],[list.department],[list.designation],[list.FAP_2021_Score],[list.FRS_2021],[list.FRP_2021],[list.Feedback_2021_Score]]),
       
        
      })
    

    doc.save('table.pdf'); 

  };
    

  const fileType = 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet;charset=UTF-8';
  const fileExtension = '.xlsx';
  const fileName="download"

  const exportToCSV = (list, fileName) => {
      const ws = XLSX.utils.json_to_sheet(list);
      const wb = { Sheets: { 'data': ws }, SheetNames: ['data'] };
      const excelBuffer = XLSX.write(wb, { bookType: 'xlsx', type: 'array' });
      const data = new Blob([excelBuffer], {type: fileType});
      FileSaver.saveAs(data, fileName + fileExtension);
  }


  return (
    <div className="display1">
  <div className="results-list-page-nav">
<p>Navigate</p>
<button data-operation="-" onClick={changeHandler}>
    &lt;{individualIndex+1}-
  </button>{" "}
  {/* {Index.start} - {Index.end} */}
  <button data-operation="+" onClick={changeHandler}>
  {vals.length}&gt;
  </button>{" "}
  </div>
    <div className="results-data-container">
      
          
      <center><img src="https://upload.wikimedia.org/wikipedia/en/7/77/Bannari_Amman_Institute_of_Technology_logo.png" style={{width:'120px',height:'70px',textAlign:'center',borderRadius:'50%',objectFit:'contain'}}/></center>
      <span className="result-data-name">{data.name}</span>
      <span className="result-data-department">{data.department}</span>
      <span className="particular"><p><h4>FACULTY INFORMATION</h4></p></span>
      {  Object.keys(data).map((x,faculty_id) => {
        return(
          //  typeof data[x] === 'string' && data[x].toLowerCase().includes('nan') ? '':
          
          fieldGroup.indexOf(x) > 0 ? '' :
          <div key={faculty_id}>

        <p>
          
        <span className="results-data-title"><p><b>{x}</b></p> </span>
        <span className="results-data-value"> <p>:{ data[x]}</p></span>
      </p>
      </div>
      )})}
<span className="details">DETAILS</span>
{/* <div style={{display:'grid',gridTemplatRows:'1fr 1fr 1fr 1fr',gridGap:'1rem'}}> */}
<div className="tab" style={{display:'grid',gridTemplateColumns:'2fr 1fr 1fr 1fr' ,gridGap:'1rem'}}>
<span className="particulars"><p><h4 style={{color:'blue',fontWeight:'bold'}}>PARTICULARS</h4></p>
{/* {
  fieldGroup.map(x=> <p style={{color: '#000'}}>{x}</p>)
} */}
    <  p >Faculty Action Plan</ p >
    < p  >Faculty FeedBack Score</  p >
    < p  >Faculty Reward Points</ p >
    < p  >Faculty Reliability Score</ p >
  </span>


<div><p><h4>2019-2020</h4></p>
<br/>
{<div className="task">
{
  Object.keys(fieldGroup).length>0 && fieldGroup.filter((x)=>x.includes('2020')).map((x,index)=> <div key={index} style={{display:'flex', flexDirection:'column'}}>{data[x]}</div>)
}
</div>}
</div>
<div><p><h4>2020-2021</h4></p><br/>
{<div className="task">
{
  Object.keys(fieldGroup).length>0 && fieldGroup.filter((x)=>x.includes('2021')).map((x,index)=> <div key={index} style={{display:'flex', flexDirection:'column'}}>{data[x]}</div>)
}
</div>}
</div>
<div><p><h4>2021-2022</h4></p><br/>
<div className="task">
{
  Object.keys(fieldGroup).length>0 && fieldGroup.filter((x)=>x.includes('2122')).map((x,index)=> <div key={index} style={{display:'flex', flexDirection:'column'}}>{data[x]}</div>)
}
</div></div>



    </div>
    <div>
    <button onClick={generatePdf}style={{padding:'7px 10px',margin:'7px',textAlign:"center",backgroundColor:'lightgreen',fontSize:'17px',width:"140px",height:"45px",borderRadius:"20px"}}>      <RiFileDownloadFill size="20px"/>
PDF FILE</button><br/>
      <button className="button" onClick={(e)=>exportToCSV(list,fileName)}style={{padding:'7px 10px',margin:'7px',textAlign:"center",backgroundColor:'lightblue',fontSize:'17px',height:"45px",width:"140px",borderRadius:"20px"}}><FcDownload size="25px"/>
EXCEL</button>
    </div>
    </div>

    </div>
  );
}


